mapper.select:
		select prd_id, category_id, sel_id, prd_name, prd_price, prd_comment
    from (
        select prd_id, category_id, sel_id, prd_name, prd_price, prd_comment,
            row_number() over(order by prd_id desc) as rNum
        from tbl_prd_b where 1=1

        ) tpb
    where rNum between #{pageStart} and #{pageEnd}
        order by prd_id desc

		select prd_id, category_id, sel_id, prd_name, prd_price, prd_comment
    from (
        select prd_id, category_id, sel_id, prd_name, prd_price, prd_comment,
            row_number() over(order by prd_id desc) as rNum
        from tbl_prd where 1=1

        ) tp
    where rNum between #{pageStart} and #{pageEnd}
        order by prd_id desc

mapper.update:
update tbl_prd set
	prd_ea = #{prd_ea}, prd_name = #{prd_name}, prd_thumb = #{prd_thumb}, prd_comment = #{prd_comment},
	prd_img = #{prd_img}, prd_stock = #{prd_stock}, prd_price = #{prd_price}, prd_sale = #{prd_sale}, prd_kg = #{prd_kg},
	prd_from = #{prd_from}, prd_wrap = #{prd_wrap}, prd_info = #{prd_info}
where prd_id = #{prd_id}

mapper.select:
select tp.prd_id, tp.prd_img, tp.prd_name, tp.prd_kg, tp.prd_price, tp.prd_comment, tc.category_id, tc.category_p_id
from tbl_prd tp inner join tbl_category tc on tp.category_id = tc.category_id
where tc.category_p_id = #{category_p_id}
